if( ! function_exists( 'feminine_business_get_image_sizes' ) ) :
	/**
	 * Get information about available image sizes
	 */
	function feminine_business_get_image_sizes( $size = '' ) {
		
		global $_wp_additional_image_sizes;
		
		$sizes = array();
		$get_intermediate_image_sizes = get_intermediate_image_sizes();
		
		// Create the full array with sizes and crop info
		foreach( $get_intermediate_image_sizes as $_size ) {
			if ( in_array( $_size, array( 'thumbnail', 'medium', 'medium_large', 'large' ) ) ) {
				$sizes[ $_size ]['width'] = get_option( $_size . '_size_w' );
				$sizes[ $_size ]['height'] = get_option( $_size . '_size_h' );
				$sizes[ $_size ]['crop'] = (bool) get_option( $_size . '_crop' );
			} elseif ( isset( $_wp_additional_image_sizes[ $_size ] ) ) {
				$sizes[ $_size ] = array( 
					'width' => $_wp_additional_image_sizes[ $_size ]['width'],
					'height' => $_wp_additional_image_sizes[ $_size ]['height'],
					'crop' =>  $_wp_additional_image_sizes[ $_size ]['crop']
				);
			}
		} 
		// Get only 1 size if found
		if ( $size ) {
			if( isset( $sizes[ $size ] ) ) {
				return $sizes[ $size ];
			} else {
				return false;
			}
		}
		return $sizes;
	}
endif;

if( ! function_exists( 'feminine_business_single_reading_calc' ) ) :
    /**
     * Reading calculation
     */
    function feminine_business_single_reading_calc( $content ){ 
        $clean_content = strip_shortcodes( $content );
        $clean_content = strip_tags( $clean_content );
        $word_count    = str_word_count( $clean_content );
        $time          = ceil( $word_count / 200 );
		if( $time ){ ?>
            <div class="time">
                <?php echo absint( $time ) . esc_html__( ' Min Read', 'feminine-business' ); ?>
            </div>
        <?php 
		}
    }
endif;

if ( ! function_exists( 'feminine_business_category' ) ) :
	/**
	 * Prints categories
	 */
	function feminine_business_category(){
		// Hide category and tag text for pages.
		if ( 'post' === get_post_type()) {
			/* translators: used between list items, there is a space after the comma */
			$categories_list = get_the_category();
			if( $categories_list ){
				echo '<span class="category" itemprop="about">';
				foreach( $categories_list as $cat_list ){
					echo '<a href="' . esc_url( get_category_link( $cat_list->term_id ) ) . '">' . esc_html($cat_list->name) . '</a>';
				}
				echo '</span>';
			}
		}
	}
endif;